import React, {useState, useEffect} from 'react';
import styled from 'styled-components';

const Container = styled.div`
display: flex;
height: 20vh;
width: 100%;
justify-content:center;
align-items:center;
background-color: ${props=> props.theme.colors.gray};

.adjContainer{
    display: flex;
    height: 50%;
    width: 50%;
    background-color: ${props=> props.theme.colors.white};
    border-radius: 8px;
    justify-content:center;
    align-items:center;
}
`

export const HeaderSection = props => {
const adjs = ['Driven', 'attentive', 'organized', 'curious', 'insipred', 'unity', 'imperfect', 'dedicated', 'innovative', 'balanced', 'communicating','action', 'aware', 'accomplishing', 'wise', 'human', 'mindful', 'intelligent', 'reflecting', 'endurance', 'integrity', 'spirit', 'abundance', 'life force', 'pure', 'questioning', 'answering', 'vibration', 'Developer']
    const [state, setState] = useState({
        currentAdj: adjs[0]
    })

    const updateAdj = () => {
        function setWord(i){
            console.log()
            setTimeout(()=> setState({...state, currentAdj: adjs[i]}), 3000)
        }
        for(let i = 1; i <= adjs.length; i++){
                console.log(adjs[i])
                setWord(i)
                i++;
        }
    }

    useEffect(()=>{
        setInterval(updateAdj(), 5000)
    },[])
    return(
        <Container>
            <div className="adjContainer">
                <h1>I am {`${state.currentAdj}`}</h1>
            </div>

        </Container>
    )
}
